jQuery(document).ready(function(){jQuery('.like, .unlike').live('click',function(){var type=jQuery(this).attr('class');var id=jQuery(this).attr('id');jQuery(this).addClass('loading');jQuery.post(ajaxurl,{action:'activity_like','cookie':encodeURIComponent(document.cookie),'type':type,'id':id},function(data){jQuery('#'+id).fadeOut(100,function(){jQuery(this).html(data).removeClass('loading').fadeIn(100)});if(type=='like'){var newID=id.replace("like","unlike");jQuery('#'+id).removeClass('like').addClass('unlike').attr('title','Unlike this item').attr('id',newID)}else{var newID=id.replace("unlike","like");jQuery('#'+id).removeClass('unlike').addClass('like').attr('title','Like this item').attr('id',newID)}if(data=='Like'){var pureID=id.replace("unlike-activity-","");jQuery('.view-likes#view-likes-'+pureID).remove();jQuery('.users-who-like#users-who-like-'+pureID).remove()}});return false});jQuery('.view-likes').live('click',function(){var type=jQuery(this).attr('class');var id=jQuery(this).attr('id');var parentID=id.replace("view-likes","users-who-like");if(!jQuery(this).hasClass('open')){jQuery(this).addClass('loading');jQuery.post(ajaxurl,{action:'activity_like','cookie':encodeURIComponent(document.cookie),'type':type,'id':id},function(data){jQuery('#'+id).html('Hide likes').removeClass('loading').addClass('open');jQuery('#'+parentID).html(data).slideDown('fast')});return false}else{jQuery(this).html('View likes').removeClass('loading, open');jQuery('#'+parentID).slideUp('fast');return false}})});